var config = require('config');

var level = config.logger.level;
//1.调试级别
exports.debug = function (msg) {
    if (level > 1) return;
    console.log('调试:' + msg);
};
//2.消息
exports.info = function (msg) {
    if (level > 2) return;
    var now = new Date;
    console.log('消息:[' + now.getHours() + ':' + now.getMinutes() + ':' + now.getSeconds() + '.' + now.getMilliseconds() + ']' + msg);
};
//3.警告
exports.warn = function (msg) {
    if (level > 3) return;
    console.log('警告:' + msg);
};
//4.错误
exports.error = function (msg) {
    if (level > 4) return;
    console.log('错误:' + msg);
};
//5.致命
exports.fatal = function (msg) {
    //if(level>5)return;
    console.log('致命:' + msg);
};

//设置级别
exports.setLevel = function (num) {
    level = num;
}